function t(t,n="YYYY-MM-DD HH:mm:ss"){if(!t)return"";const s="string"==typeof t?new Date(t):t;if(isNaN(s.getTime()))return t;const r=s.getFullYear(),Y=s.getMonth()+1,e=s.getDate(),f=s.getHours(),i=s.getMinutes(),o=s.getSeconds(),D=t=>t<10?"0"+t:t;return n.replace("YYYY",r).replace("MM",D(Y)).replace("DD",D(e)).replace("HH",D(f)).replace("mm",D(i)).replace("ss",D(o))}export{t as f};
