function formatDateTime(e,t="YYYY-MM-DD HH:mm:ss"){if(!e)return"";const r="string"==typeof e?new Date(e):e;if(isNaN(r.getTime()))return e;const a=r.getFullYear(),n=r.getMonth()+1,s=r.getDate(),c=r.getHours(),o=r.getMinutes(),i=r.getSeconds(),padZero=e=>e<10?"0"+e:e;return t.replace("YYYY",a).replace("MM",padZero(n)).replace("DD",padZero(s)).replace("HH",padZero(c)).replace("mm",padZero(o)).replace("ss",padZero(i))}export{formatDateTime as f};
